package arithmatic_operation;

public class All_n_one 
{

	public static void main(String[] args) 
	{
		float a=85 , b=17;
		System.out.println("Sum of these numbers =" +(a+b));
		System.out.println("Subtraction of these numbers =" +(a-b));
		System.out.println("Multiplication of these numbers =" +(a*b));
		System.out.println("Division of these numbers1. =" +(a/b));
		System.out.println("Division of these numbers2. =" +(b/a));
		System.out.println("Remainder of these numbers1. =" +(a%b));
		System.out.println("Remainder of these numbers2. =" +(b%a));
		// TODO Auto-generated method stub

	}

}
